select name, Score, `Balls Thrown`, `Extra`, `Wicket` from
(select sum(score) "Score", count(distinct ball_number) "Balls Thrown", count(extra) "Extra", count(wicket_type) "Wicket", bowler, bowling_team from BallEvents where match_id = ? group by bowler, bowling_team) as BowlingScoreBoard
    inner join Player p on BowlingScoreBoard.bowler = p.player_order and BowlingScoreBoard.bowling_team = p.team_id;